import React, { useEffect, useState } from 'react';

import type { PropsWithChildren } from 'react';
import {
  Image,
  SafeAreaView,
  ScrollView,
  StatusBar,
  StyleSheet,
  Text,
  useColorScheme,
  View,
  TouchableOpacity,
  FlatList,
  TextInput,
  Button,
  Modal
} from 'react-native';

import {
  Colors,
  DebugInstructions,
  Header,
  LearnMoreLinks,
  ReloadInstructions,

} from 'react-native/Libraries/NewAppScreen';
import { users, User } from './models/Users';

// import Icon from 'react-native-vector-icons/FontAwesome';

type SectionProps = PropsWithChildren<{
  title: string;
}>;

function Section({ children, title }: SectionProps): React.JSX.Element {
  const isDarkMode = useColorScheme() === 'dark';
  return (
    <View style={styles.sectionContainer}>
      <Text
        style={[
          styles.sectionTitle,
          {
            color: isDarkMode ? Colors.white : Colors.black,
          },
        ]}>
        {title}
      </Text>
      <Text
        style={[
          styles.sectionDescription,
          {
            color: isDarkMode ? Colors.light : Colors.dark,
          },
        ]}>
        {children}
      </Text>
    </View>
  );
}

function App(): React.JSX.Element {
  const isDarkMode = useColorScheme() === 'dark';

  const backgroundStyle = {
    backgroundColor: isDarkMode ? Colors.darker : Colors.lighter,
  };

  const [usersData, setUsersData] = useState<User[]>([]);
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [filteredUsers, setFilteredUsers] = useState<User[]>([]);
  const [isNewUserModalVisible, setIsNewUserModalVisible] = useState<boolean>(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [newUser, setNewUser] = useState<User>({
    id: 0,
    username: '',
    email: '',
    password: '',
    full_name: '',
    address: '',
    phone_number: '',
    date_of_birth: '',
  });
  const [isEditUserModalVisible, setIsEditUserModalVisible] = useState<boolean>(false);
  const [editedUser, setEditedUser] = useState<User>({
    id: 0,
    username: '',
    email: '',
    password: '',
    full_name: '',
    address: '',
    phone_number: '',
    date_of_birth: '',
  });

  useEffect(() => {
    setUsersData(users);
    setFilteredUsers(users);
  }, [])

  const handleSearch = (text: string) => {
    setSearchTerm(text);
    const filtered = users.filter(
      user =>
        user.username.toLowerCase().includes(text.toLowerCase()) ||
        user.email.toLowerCase().includes(text.toLowerCase()) ||
        user.full_name.toLowerCase().includes(text.toLowerCase())
    );
    setFilteredUsers(filtered);
  };

  const handleAddUser = () => {
    setIsNewUserModalVisible(true);
  };

  const handleSaveUser = () => {
    setUsersData([...usersData, newUser]);
    setFilteredUsers([...usersData, newUser]);
    setIsNewUserModalVisible(false);
    setNewUser({
      id: 0,
      username: '',
      email: '',
      password: '',
      full_name: '',
      address: '',
      phone_number: '',
      date_of_birth: '',
    });
  };

  const handleEditUser = (user: User) => {
    setEditedUser(user);
    setIsEditUserModalVisible(true);
  };

  const handleSaveEditedUser = () => {
    const updatedUsers = usersData.map(user =>
      user.id === editedUser.id ? { ...user, ...editedUser } : user
    );
    setUsersData(updatedUsers);
    setFilteredUsers(updatedUsers);
    setIsEditUserModalVisible(false);
  };

  const handleDeleteUser = (userId: number) => {
    const updatedUsers = usersData.filter(user => user.id !== userId);
    setUsersData(updatedUsers);
    setFilteredUsers(updatedUsers);
  };


  const renderUserItem = ({ item }: { item: User }) => (
    <TouchableOpacity style={styles.userItem} onPress={() => handleEditUser(item)}>
      <Text>USERNAME: {item.username}</Text>
      <Text>EMAIL: {item.email}</Text>
      <Text>PASSWORD: {item.password}</Text>
      <Text>FULL NAME: {item.full_name}</Text>
      <Text>ADDRESS: {item.address}</Text>
      <Text>PHONE: {item.phone_number}</Text>
      <Text>BIRTHDAY: {item.date_of_birth}</Text>
      <Button title="Delete" color="#ff0000" onPress={() => handleDeleteUser(item.id)} />
      </TouchableOpacity>
  );



  return (
    <SafeAreaView style={backgroundStyle}>
      <StatusBar
        barStyle={isDarkMode ? 'light-content' : 'dark-content'}
        backgroundColor={backgroundStyle.backgroundColor}
      />
      <ScrollView
        contentInsetAdjustmentBehavior="automatic"
        style={backgroundStyle}>
        <View style={styles.container}>
          <Text style={styles.sectionTitle}>USERS MANAGEMENT</Text>
          <TextInput
            style={styles.searchInput}
            placeholder="Search"
            onChangeText={handleSearch}
            value={searchTerm}
          />
          <FlatList
            data={filteredUsers}
            renderItem={renderUserItem}
            keyExtractor={item => item.id.toString()}
          />
          <Button title="Add User" onPress={handleAddUser} />
          <Modal visible={isNewUserModalVisible} animationType="slide">
            <View style={styles.modalContainer}>
            <Text style={styles.sectionTitle}>ADD USER</Text>
              <TextInput
                style={styles.input}
                placeholder="Username"
                onChangeText={text => setNewUser({ ...newUser, username: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Email"
                onChangeText={text => setNewUser({ ...newUser, email: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Password"
                secureTextEntry={true}
                onChangeText={text => setNewUser({ ...newUser, password: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Full Name"
                onChangeText={text => setNewUser({ ...newUser, full_name: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Address"
                onChangeText={text => setNewUser({ ...newUser, address: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Phone Number"
                onChangeText={text => setNewUser({ ...newUser, phone_number: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Date Of Birth"
                onChangeText={text => setNewUser({ ...newUser, date_of_birth: text })}
              />

              <Button title="Save" color="#00ff51" onPress={handleSaveUser} />
              <Button title="Cancel" color="#949494" onPress={() => setIsNewUserModalVisible(false)} />
            </View>
          </Modal>
          <Modal visible={isEditUserModalVisible} animationType="slide">
            <View style={styles.modalContainer}>
              <Text style={styles.sectionTitle}>EDIT USER</Text>
              <TextInput
                style={styles.input}
                placeholder="Username"
                value={editedUser.username}
                onChangeText={text => setEditedUser({ ...editedUser, username: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Email"
                value={editedUser.email}
                onChangeText={text => setEditedUser({ ...editedUser, email: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Password"
                value={editedUser.password}
                secureTextEntry={true}
                onChangeText={text => setEditedUser({ ...editedUser, password: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Full Name"
                value={editedUser.full_name}
                onChangeText={text => setEditedUser({ ...editedUser, full_name: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Address"
                value={editedUser.address}
                onChangeText={text => setEditedUser({ ...editedUser, address: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Phone Number"
                value={editedUser.phone_number}
                onChangeText={text => setEditedUser({ ...editedUser, phone_number: text })}
              />
              <TextInput
                style={styles.input}
                placeholder="Date Of Birth"
                value={editedUser.date_of_birth}
                onChangeText={text => setEditedUser({ ...editedUser, date_of_birth: text })}
              />
              <Button title="Save" color="#00ff51" onPress={handleSaveEditedUser} />
              <Button title="Cancel" color="#949494" onPress={() => setIsEditUserModalVisible(false)} />
            </View>
          </Modal>
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  sectionContainer: {
    marginTop: 32,
    paddingHorizontal: 24,
  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: '600',
  },
  sectionDescription: {
    marginTop: 8,
    fontSize: 18,
    fontWeight: '400',
  },
  highlight: {
    fontWeight: '700',
  },
  container: {
